<template>
  <v-row justify="center">
    <v-dialog
      v-model="dialog"
      persistent
      max-width="390"
    >
      <template #activator="{ on, attrs }">
        <v-btn
          color="red"
          dark
          v-bind="attrs"
          v-on="on"
        >
          Conect
        </v-btn>
      </template>
      <v-card>
        <v-card-title class="text-h6">
          Gracias por el Cafe!
          <v-spacer></v-spacer>
            <v-btn
                v-if="mostrar"
                color="green darken-1"
                text
                @click="login()"
            >
                Conect
            </v-btn>
        </v-card-title>
        <v-col v-show="account" class="text-center">
                <v-avatar>
                    <img src="data:image/png;base64," alt="">
                </v-avatar>
            <v-card-text>
            {{account}}
            </v-card-text>
        </v-col>
        <v-card-actions>
            <v-form>
                <v-row>
                    <v-text-field
                        v-model="sendAccount"
                        label="Cuenta"
                        placeholder="Num"
                        filled
                        disabled
                        dense
                    ></v-text-field>
                    <v-text-field
                        v-model="AmountValue"
                        label="Monto"
                        placeholder="0"
                        filled
                        dense
                    ></v-text-field>
                </v-row>
            </v-form>
        </v-card-actions>
        <v-card-actions>
          <v-btn
            color="green darken-1"
            text
            @click="dialog = false"
          >
            Cancelar
          </v-btn>
          <v-spacer></v-spacer>
          <v-btn
            color="green darken-1"
            text
            :disabled="!account"
            @click="enviar()"
          >
            Enviar
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>
  </v-row>
</template>

<script>
import Wb3 from 'web3'
let web3 = require('web3')

  export default {
    data () {
      return {
        dialog: false,
        account: '',
        sendAccount: '0x157dEE9A4bD1620A38c804e0f08219695Cf12aD5',
        AmountValue: '',
        mostrar: true
      }
    },
    methods: {
        enviar() {
            if(Number(this.AmountValue) <= 0) {
                alert('Valor no permitido')
            }
            if(!web3.utils.isAddress(this.sendAccount)) {
                alert('Direccion no valida')
            }
            web3.eth.sendTransaction({
                from: this.sendAccount,
                to: this.account,
                value: this.AmountValue
            })
        },
        async login() {
            if(window.ethereum) {
            try {
                await window.ethereum.request({method: 'eth_requestAccounts'})
                web3 = new Wb3(window.ethereum)
                const cuenta = await web3.eth.getAccounts()
                this.account = cuenta[0]
                console.log(cuenta)
                this.mostrar = false
            } catch (error) {
                console.log(error)
                alert('No conectado')
            }
            } else {
                console.log('no conect')
            }
        }
    }
  }
</script>
